mapper.insert:
SELECT SEQ_COMMENT_NO.NEXTVAL FROM DUAL
INSERT INTO "COMMENT" VALUES( #{commentNo}, #{commentContent}, DEFAULT, DEFAULT, #{memberNo}, #{boardNo},
NULL
#{parentCommentNo}
)

mapper.update:
