mapper.delete:
		DELETE FROM BOARD_LIKE
		WHERE BOARD_NO IN

            #{postId}

        AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE BOARD
SET BOARD_DEL_FL = 'Y'
WHERE BOARD_NO IN

    #{postId}

AND MEMBER_NO = #{memberNo}

UPDATE "COMMENT"
SET COMMENT_DEL_FL = 'Y'
WHERE COMMENT_NO IN

    #{commentId}

AND MEMBER_NO = #{memberNo}
